import React from 'react'
import "./loginregister.css"

export default function LoginRegister() {

    const byId = (id) => {
        return document.getElementById(id);
    };

    const $signUpbutton = byId('signUp');
    const $signInbutton = byId('signIn');
    const $container = byId('container');

    $signUpbutton.addEventListener(
        'click',
        () => {
            $container.classList.add 
            ('right-panel-active');
        },
    );

    $signInbutton.addEventListener(
        'click',
        () => {
            $container.classList.add(
                'right-panel-active'
            );
        },
    );

  return (
    <div className="bigContainer">
        <div className="container" id='container'>
            <div className="signUpContainer 
            formContainer">
                <form action="" className='form' >
                    <h1 className="headerOne">Create Account</h1>
                    <span className="loginForgot">Use your email to register</span>
                    <input type="text" placeholder='Username' className='inputField'/>
                    <input type="email" placeholder='email' className='inputField'/>
                    <input type="password" placeholder='password' className='inputField'/>
                    <input type="password" placeholder='password again' className='inputField'/>
                    <button className='button'>Sign Up</button>
                </form>
            </div>
            <div className="signInContainer formContainer">
                <form action="" className='form'>
                    <h1 className="headerOne">Sign In</h1>
                    <span className="loginForgot">Login to you account</span>
                    <input type="email" placeholder="Email" className='inputField' />
                    <input type="password" placeholder='Enter your password' className='inputField' />
                    <span className="loginForgot">Forgot password?</span>
                    <button className='button'>Sign In</button>
                </form>
            </div>
            <div className="overlayContainer">
                <div className="overlay">
                    <div className="overlayPanel overlayPanelLeft">
                        <h1 className="headerOne">Welcome back</h1>
                        <p>To keep connected with us please login to your account</p>
                        <button className="button ghost" id="signIn">Sign In</button>
                    </div>
                    <div className="overlayPanel overlayPanelRight">
                        <h1 className="headerOne">Hello, Friend!</h1>
                        <p>Enter your personal details and connect with people all over the world</p>
                        <button className="button ghost" id="signUp">Sign Up</button>                
                    </div>
                </div>
            </div>
        </div>
    </div>
  )
}
