'use strict';

function capitalizeFirstLetter(stringChar) {
    return stringChar
        .charAt(0)
        .toUpperCase() + stringChar.slice(1);
}
function isInDateRange(day, from, to) {
    const check = new Date(day);
    return (check.getTime() <= to.getTime() && check.getTime() >= from.getTime()) ? true : false;
}
// https://codereview.stackexchange.com/questions/184459/getting-the-date-on-yyyymmdd-format
function yyyymmdd(date) {
    const x = date;
    const y = x.getFullYear().toString();
    let m = (x.getMonth() + 1).toString();
    let d = x.getDate().toString();
    (d.length === 1) && (d = '0' + d);
    (m.length === 1) && (m = '0' + m);
    const yyyymmdd = y + '-' + m + '-' + d;
    return yyyymmdd;
}

exports.capitalizeFirstLetter = capitalizeFirstLetter;
exports.isInDateRange = isInDateRange;
exports.yyyymmdd = yyyymmdd;
