import { r as registerInstance } from './index-c0dc768b.js';

const MaterialsAlertController = class {
    constructor(hostRef) {
        registerInstance(this, hostRef);
    }
    /** Create a HTMLMaterialsDialogElement and returns it */
    async create(opts) {
        const dialog = document.createElement('materials-dialog');
        dialog.dialogTitle = opts.title;
        dialog.actions = [];
        if (opts.cancelText) {
            dialog.actions.push({
                label: opts.cancelText,
                role: 'close',
                action: opts.onCancel
            });
        }
        dialog.actions.push({
            label: opts.acceptText,
            role: 'accept',
            action: opts.onAccept
        });
        dialog.body = opts.message;
        dialog.addEventListener('accept', () => dialog.remove());
        dialog.addEventListener('cancel', () => dialog.remove());
        document.body.appendChild(dialog);
        return dialog;
    }
};

export { MaterialsAlertController as materials_alert_controller };
